mapper.update:
UPDATE user
SET user_account = #{account}
WHERE user_email = #{user_email}

mapper.select:
SELECT * FROM auction
upper(auction_title) like '%' || upper(#{searchWord}) || '%'
upper(auction_content) like '%' || upper(#{searchWord}) || '%'
user_email = #{searchWord}
upper(auction_title) like '%' || #{searchWord} || '%' or upper(auction_content) like '%' || #{searchWord} || '%' or user_email = #{searchWord}

SELECT * FROM auction
upper(auction_title) like '%' || upper(#{searchWord}) || '%'
upper(auction_content) like '%' || upper(#{searchWord}) || '%'
user_email = #{searchWord}
upper(auction_title) like '%' || #{searchWord} || '%' or upper(auction_content) like '%' || #{searchWord} || '%' or user_email = #{searchWord}
AND auction_status = '거래 중' AND auction_finish >= now()

select count(*) from auction
upper(auction_title) like '%' || upper(#{searchWord}) || '%'
upper(auction_contents) like '%' || upper(#{searchWord}) || '%'
user_email = #{searchWord}
upper(auction_title) like '%' || upper(#{searchWord}) || '%' or upper(auction_contents) like '%' || upper(#{searchWord}) || '%' or user_email = #{searchWord}

