mapper.insert:
        insert into
			location
		(
			loc_id
			,board_no
			,loc_name
			,lat
			,lng
		)
		values
		(
			func_seq_10('LOCA')
			,#{board_no}
			,#{loc_name}
			,#{lat}
			,#{lng}
		)

mapper.update:
update
	location
SET
	loc_name = #{loc_name}
	,lat = #{lat}
	,lng = #{lng}
where
	loc_id = #{loc_id}
