mapper.update:
UPDATE user
SET user_account = #{account}
WHERE user_email = #{user_email}

mapper.insert:
    	 INSERT INTO
    		used(
	    		used_id
				,user_email
				,category_id
				,used_title
				,used_price
				,used_content
				,used_status
				,used_quality
				,uloc_id
				,sloc_id
    		    )
    	VALUES(
	    		func_seq_10('USED')
    			,#{user_email}
    			,#{category_id}
    			,#{used_title}
    			,#{used_price}
    			,#{used_content}
    			,'거래 중'
    			,#{used_quality}
    			,#{uloc_id}
				,#{sloc_id}
    			)

    		SELECT 
    			used_id 
    		from 
    			used
    		order by
    			used_date desc
    		LIMIT 1

INSERT INTO
