mapper.select:
select count(*) from used
used_status = 'NEW'
and upper(used_title) like '%' || upper(#{searchWord}) || '%'
and upper(used_contents) like '%' || upper(#{searchWord}) || '%'
and user_email = #{searchWord}
and upper(used_title) like '%' || upper(#{searchWord}) || '%' or upper(used_contents) like '%' || upper(#{searchWord}) || '%' or user_email = #{searchWord}

mapper.update:
    	UPDATE used
	    SET used_status = '거래 중지'
	    WHERE used_id = #{used_id}

mapper.select:
SELECT * FROM used
upper(used_title) like '%' || upper(#{searchWord}) || '%'
upper(used_content) like '%' || upper(#{searchWord}) || '%'
user_email = #{searchWord}
upper(used_title) like '%' || #{searchWord} || '%' or upper(used_content) like '%' || #{searchWord} || '%' or user_email = #{searchWord}

mapper.update:
    	UPDATE used_buy
	    SET uBuy_status = '거래 중지'
	    WHERE uBuy_id = #{uBuy_id}

UPDATE
	used_buy
SET
	 uBuy_title = #{uBuy_title}

	 ,uBuy_content = #{uBuy_content}
WHERE
	uBuy_id = #{uBuy_id}

