mapper.insert:
INSERT INTO user 
(
	user_email
	,user_name
	,user_nick
	,user_pwd
	,user_phone
	,user_post
	,user_addr1
	,user_addr2
	,user_nation
)
VALUES
(
	#{user_email}
	, #{user_name}
	, #{user_nick}
	, #{user_pwd}
	, #{user_phone}
	, #{user_post}
	, #{user_addr1}
	, #{user_addr2}		
	, #{user_nation}
);

insert INTO 
	user_dummy
(
	user_email
	,user_nick
	,nation
)
VALUES
(
	#{user_email}
	, #{user_nick}
	, #{user_nation}
);

insert INTO
	user_character
(
	uChar_id
	, user_email
	, char_id
	, char_level
	, char_selected
)
VALUES
(
	func_seq_10('USER')
	, #{user_email}
	, 'CHAR000000'
	, 1
	, 1
);

mapper.update:
        update
			user
		set

			user_nick = #{user_nick}


			,user_phone = #{user_phone}


			,user_post = #{user_post}


			,user_addr1 = #{user_addr1}


			,user_addr2 = #{user_addr2}


			,user_pwd = #{user_pwd}

		where
			user_email = #{user_email};
		update
			user_dummy
		set

			user_nick = #{user_nick}

		where
			user_email = #{user_email};
